graphql.query:
query find($token: String!, $params: String){
    find (token: $token, params: $params){
        stats {totalHits searchEngineTimeInMillis searchEngineRoundTripTimeInMillis searchProcessingTimeInMillis}
        documentList {
            id
            displayName
            numberOfHits
            documents {
              _id
              _type
              doc_date_time
              docid
              main_content_url
              mmid
              pdf_preview_standard_url
              pdf_preview_url
              png_highpreview1278_url
              png_highpreview800_url
              png_highpreview_url
              png_lowpreview_url
              taxonomy
              thumbnail_url
              title
            }
            pagination{
                offset
                hitsPerPage
                firstPage {displayName selected query}
                previousPage {displayName selected query}
                nextPage {displayName selected query}
                pages {displayName selected query}
            }
            sortOptions {displayName selected query}
        }
        facets {
            id
            displayName
            selectionType
            multipleLevels
            filters { 
                displayName
                selected
                query 
                count
                properties {id parentId}
            }
        }
    }
}

query qc($token: String!, $params: String){
    qc (token: $token, params: $params){
        stats {totalHits searchEngineTimeInMillis searchEngineRoundTripTimeInMillis searchProcessingTimeInMillis}
        documentList {
            id
            displayName
            numberOfHits
            documents {
              _id
              _type
              title
            }
        }
    }
}
